const fs = require('fs')
const path = require('path')

const { remote } = require('electron')
const mainWindow = remote.getCurrentWindow()
const dialog = remote.dialog
const shell = remote.shell

const fileLocation = $data.filePath
const report = $data.report

cti.debugLog(`:: ATTEMPTING TO SAVE ${JSON.stringify(report)} FROM ${fileLocation}`)

const reportName = report.type.name || report.type.id;
const dateRange = `${moment(report.startDate).format('YYYY-MM-DD')} - ${moment(report.endDate).format('YYYY-MM-DD')}`
const requestedDate = moment(report.requested.date).format('YYYY-MM-DD')
const suggestedReportFileName = `Trauma Report - ${reportName} - ${dateRange} requested ${requestedDate}.csv`

dialog.showSaveDialog(mainWindow, {
    title: 'Select download location',
    buttonLabel: 'Save',
    defaultPath: '*/' + suggestedReportFileName
}, (saveLocation) => {
    if (saveLocation) {

        _moveDownload(fileLocation, saveLocation)
            .then(() => _openDownloadLocation(saveLocation))
            .then(() => $success())
            .catch(ex => $fail(ex))
    } else {

        _deleteDownload(fileLocation)
            .then(() => $success())
            .catch(ex => $fail(ex))
    }
});

const _moveDownload = (from, to) => {

    return new Promise(resolve => {
        cti.debugLog(`:: MOVING REPORT FROM ${from} TO ${to}`)
        fs.renameSync(from, to)
        resolve()
    })
}

const _openDownloadLocation = (location) => {

    return new Promise(resolve => {
        shell.showItemInFolder(location)
        resolve()
    })
}

const _deleteDownload = (location) => {

    return new Promise(resolve => {
        cti.debugLog(`:: DELETING REPORT AT ${location}`)
        fs.unlinkSync(location)
        resolve()
    })
}

