cti.debugLog(':: Binding hidden clicks...')

const list = ['Open_Incidents', 'Closed_Incidents', 'Landing_Page', 'User_List', 'Export']
  .map(e => {
    return `#${e} section#footer`
  })
  .join(', ')

const els = document.querySelectorAll(list)

els.forEach(el => {
  if (el.getAttribute('hidden-clicks-set') === 'true') {
    cti.debugLog(':: Footer bar click event already bound...resetting menu clicks (page has reloaded)')
    cti.store.variables.hiddenMenuClicks = 0

    return false
  }

  cti.debugLog(':: Will bind footer bar click event...')
  el.addEventListener(
    'click',
    e => {
      // Reset if this click isn't within the last 10s.
      if (!cti.store.variables.hiddenMenuStamp || Date.now() > cti.store.variables.hiddenMenuStamp + 10000) {
        cti.store.variables.hiddenMenuStamp = Date.now()
        cti.store.variables.hiddenMenuClicks = 0
      }

      cti.store.variables.hiddenMenuClicks++

      if (cti.store.variables.hiddenMenuClicks >= cti.store.variables.hiddenMenuClickLimit) {
        return (cti.store.variables.hiddenMenuClicks = 0), cti.utils.callActionflow('show_support_info')
      }
    },
    false
  )

  el.setAttribute('hidden-clicks-set', 'true')
})

$success()

if (cti.store.preferences.debugMode === "true") {
  var params = Array.prototype.slice.call(arguments);
  console.log.apply(console.log, params);
}
